locList.addLoc("pc_greenhouse_hot","Hot Reducing World","en")
locList.addLoc("pc_greenhouse_hot_desc","A rocky planet with a thick, high pressure atmosphere. The gases in the atmosphere have caused a runaway greenhouse effect, creating very high surface temperatures.","en")
