locList.addLoc("sm_sb_building_cavorite_amalgamator","Cavorite Amalgamator","en")
locList.addLoc("sm_sb_building_cavorite_amalgamator_desc","Uses Cavorite as a highly effective and reusable amalgamator, creating a significant amount of Minerals and Energy Credits.\n","en")
locList.addLoc("sm_sb_building_cryodine_purifier","Cryodine Purifier","en")
locList.addLoc("sm_sb_building_cryodine_purifier_desc","Purifies Cryodine Ice into a pure form, fueling Engineering research ventures utilizing its thermodynamic properties.\n","en")
locList.addLoc("sm_sb_building_dark_energy_accelerator","Dark Energy Accelerator","en")
locList.addLoc("sm_sb_building_dark_energy_accelerator_desc","Manipulates Dark Energy particles for experiments into unusual Physics' properties, deeply expanding knowledge on them.\n","en")
locList.addLoc("sm_sb_building_garanthium_smelter","Garanthium Smelter","en")
locList.addLoc("sm_sb_building_garanthium_smelter_desc","Smelts Garanthium with other metals to extend its useful properties, yielding a great deal of useful alloys.\n","en")
locList.addLoc("sm_sb_building_hyperdiamond_lapidary","Hyperdiamond Lapidary","en")
locList.addLoc("sm_sb_building_hyperdiamond_lapidary_desc","Cuts Hyperdiamonds into useful and aesthetically pleasing forms for both public and private purposes.\n","en")
locList.addLoc("sm_sb_building_hyperfuel_reactor","Hyperfuel Reactor","en")
locList.addLoc("sm_sb_building_hyperfuel_reactor_desc","Produces significant quantities of Energy Credits by fully utilizing our Hyperfuel reserves.\n","en")
locList.addLoc("sm_sb_building_illum_spectral_emitters","Illum Spectral Emitters","en")
locList.addLoc("sm_sb_building_illum_spectral_emitters_desc","Emits a constant field of spectral echoes and spatial distortions, confusing enemy ship sensors in the system.\n","en")
locList.addLoc("sm_sb_building_living_research_facility","Living Metal Research Facility","en")
locList.addLoc("sm_sb_building_living_research_facility_desc","Researches the mysteries of Living Metal to revolutionize our Engineering sciences.\n","en")
locList.addLoc("sm_sb_building_lythuric_reactor","Lythuric Reactor","en")
locList.addLoc("sm_sb_building_lythuric_reactor_desc","Harnesses Lythuric Gas' potent energy properties to provide both power and a potent tool for Physics experimentation.\n","en")
locList.addLoc("sm_sb_building_neodymium_integrity_field_generator","Neodymium Integrity Field Generator","en")
locList.addLoc("sm_sb_building_neodymium_integrity_field_generator_desc","Reinforces friendly ships in the system with a Neodymium bulwark integrity field, dramatically improving resistance to damage.\n","en")
locList.addLoc("sm_sb_building_neorite_targeting_array","Neorite Targeting Array","en")
locList.addLoc("sm_sb_building_neorite_targeting_array_desc","Makes use of special Neorite sensors to create an array battery, improving targeting efficacy of every friendly ship in the system.\n","en")
locList.addLoc("sm_sb_building_neutronium_forge","Neutronium Forge","en")
locList.addLoc("sm_sb_building_neutronium_forge_desc","Forges Neutronium for non-military purposes, creating extremely durable outputs for a number of industrial uses.\n","en")
locList.addLoc("sm_sb_building_orillium_factory","Orillium Factory","en")
locList.addLoc("sm_sb_building_orillium_factory_desc","Puts Orillium's properties into producing mining equipment, increasing Minerals output.\n","en")
locList.addLoc("sm_sb_building_pyronium_experimental_lab","Pyronium Experimental Lab","en")
locList.addLoc("sm_sb_building_pyronium_experimental_lab_desc","Puts the explosive Pyronium crystals to use in Physics experiments - nothing shall remain unexploded.\n","en")
locList.addLoc("sm_sb_building_scritch_gravity_forge","Scritch Gravity Forge","en")
locList.addLoc("sm_sb_building_scritch_gravity_forge_desc","Enables use of Scritch with highly advanced gravitic funneling and 'metal casting' technologies, creating extemely hard tools and materials.\n","en")
locList.addLoc("sm_sb_building_selenemite_foundry","Selenemite Foundry","en")
locList.addLoc("sm_sb_building_selenemite_foundry_desc","Refines Selenemite ore for study in the fields of bioelectricity and laser surgery.","en")
locList.addLoc("sm_sb_building_sorium_shield_multiplexer","Sorium Shield Multiplexer","en")
locList.addLoc("sm_sb_building_sorium_shield_multiplexer_desc","Enhances the shields of friendly ships nearby using a Sorium-derived multiplexer.\n","en")
locList.addLoc("sm_sb_building_teldar_lapidary","Teldar Lapidary","en")
locList.addLoc("sm_sb_building_teldar_lapidary_desc","Cuts Teldar into powerful lenses used in both Mining and Energy generation.\n","en")
locList.addLoc("sm_sb_building_thullium_energy_processor","Thullium Energy Processor","en")
locList.addLoc("sm_sb_building_thullium_energy_processor_desc","Combines Thullium with existing power-harvesting technologies to make the starbase a potent Energy hub.\n","en")
locList.addLoc("sm_sb_building_xerodine_hyperlane_stabilizer","Xerodine Hyperlane Stabilizer","en")
locList.addLoc("sm_sb_building_xerodine_hyperlane_stabilizer_desc","Assists friendly ships in the system with FTL travel by stabilizing hyperlane entry points using Xerodine, expediting hyperdrive entry.\n","en")
locList.addLoc("sm_sb_building_yurantic_mirror_core","Yurantic Mirror Core","en")
locList.addLoc("sm_sb_building_yurantic_mirror_core_desc","Duplicates Energy and signal transmission through a specialized Yurantic core, aiding our researchers in Engineering fields.\n","en")
locList.addLoc("sm_sb_building_zro_catalyzer","Zro Catalyzer","en")
locList.addLoc("sm_sb_building_zro_catalyzer_desc","Catalyzes Zro's exotic particles beyond individual use, contributing to research into widespread Social manipulation and enhancement.\n","en")
locList.addLoc("soil_harvesting","Soil Harvesting","en")
locList.addLoc("soil_harvesting_desc","$soil_harvesting$ is a $planetary_projects$ project. $planetary_projects_desc$\n\n$tech_soil_harvesting_desc$","en")
locList.addLoc("sorium_3_discovered_country","\n£sr_sorium_3 §H$sr_sorium_3$§!\n$sr_sorium_3_desc$\n\n","en")
locList.addLoc("special_resource_building_build_cost_mult","Special Resource Buildings Build Cost Multiplier","en")
locList.addLoc("special_resource_building_construction_speed_mult","Special Resource Buildings Construction Speed Multiplier","en")
locList.addLoc("sr_altair_stone","Altair Stone","en")
locList.addLoc("sr_altair_stone_desc","The marvelous Altair Stone literally levitates, defying gravity through innate dark energy radiation. While scientists argue over its origins being from extra-dimensional visitors or a stage 8 precursor civilization, it nevertheless holds deeply exploitable anti-gravitic properties.","en")
locList.addLoc("sr_archeological_remains","Archaeological Remains","en")
locList.addLoc("sr_archeological_remains_desc","This area contains archaeological remains of an extinct alien civilization.","en")
locList.addLoc("sr_aurantiacum_x","Aurantiacum-X","en")
locList.addLoc("sr_aurantiacum_x_desc","This incredible substance defies the laws of physics: at room temperature this orange substance flows like mercury, yet it becomes an incredibly resilient metal solid once heated. Even stranger, when cooled, it becomes a gas, settling into a plasmic state bordering on absolute zero. It has been theorized that this material originated in another universe entirely, and its appearance in our universe has been the source of intense debate and speculation.","en")
locList.addLoc("sr_bandersnatchi","Bandersnatchi","en")
locList.addLoc("sr_bandersnatchi_desc","These immense furry creatures appear to have been seeded across the stars by an unknown precursor civilization. Their genetic makeup uniquely combines artificial XNA segments with genetic material derived from over two dozen biomes. Despite showing remarkable intelligence and receptive socialization with other species, attempts at sapient-level communication has proved impossible. Given their delicious taste, high-energy fat reserves and docile nature, they are typically used as livestock, biofuel, or performance animals.","en")
locList.addLoc("sr_betharian_stone","Betharian Stone","en")
locList.addLoc("sr_betharian_stone_desc","A semi-organic oil shale formed under specific yet commonly found conditions, Betharian stone can be refined into a highly compact energy source.","en")
locList.addLoc("sr_biofuels","Bio-Fuels","en")
locList.addLoc("sr_biofuels_desc","The indigenous vegetation in this area can be easily converted into potent bio-fuel.","en")
locList.addLoc("sr_blooming_ore","Blooming Ore","en")
locList.addLoc("sr_blooming_ore_desc","This curious metallic ore, found in flower-like formations, produces red-colored circuitry with unparalleled signal reliability and heat resistance, vastly improving robotics and AI efficiency.","en")
locList.addLoc("sr_cavorite","Cavorite Ore","en")
locList.addLoc("sr_cavorite_desc","Cavorite is a semi-liquid metal that can absorb high amounts of harmful radiation, making it essential for operations in high energy environments, and an ideal fit for the shielding starbases.","en")
locList.addLoc("sr_chromium","Chromium","en")
locList.addLoc("sr_chromium_desc","Chromium is an exceedingly hard metal, proving highly useful in the construction of ship hulls.","en")
locList.addLoc("sr_cryodine","Cryodine Ice","en")
locList.addLoc("sr_cryodine_desc","This substance, upon its initial discovery, was believed to have nearly unlimited thermal capacity. It was later proven that it can somehow transfer stored energy across vast distances, even through vacuums. This makes cryodine a perfect coolant.","en")
locList.addLoc("sr_dark_matter_fuel_rods","Dark Matter Fuel Rods","en")
locList.addLoc("sr_dark_matter_fuel_rods_desc","Extracting Dark Matter is only the first step to attaining the most powerful energy source in the galaxy. For the most advanced applications, it must first be housed and stabilized in transportable fuel rods. Once done, however, these fuel rods can power all manner of projects and buildings.","en")
locList.addLoc("sr_elder_compounds","Elder Compounds","en")
locList.addLoc("sr_elder_compounds_desc","This area contains large quantities of unique compounds created by an unknown precursor species. Their fabrication technology has been utterly lost, but the priceless compounds are even now still highly durable and reusable.","en")
locList.addLoc("sr_engos_vapor","Engos Vapor","en")
locList.addLoc("sr_engos_vapor_desc","Engos Vapor is the gaseous secretion of an engineered bacterium, which seems to have been intentionally seeded across worlds by an unknown precursor species. The remarkable vapor possesses unique radiation absorption properties, deeply aiding both colonization and high-radiation industrial operations.","en")
locList.addLoc("sr_euratite","Euratite","en")
locList.addLoc("sr_euratite_desc","Somehow present solely on this planet, Euratite seems to have come from an entirely separate dimension. Its sub-spatial origins make it impossible to transport in its raw form, necessitating a complex, energy-intensive transmutative process to exploit its properties.","en")
locList.addLoc("sr_ever_blossom","Ever-Blossom","en")
locList.addLoc("sr_ever_blossom_desc","The blossoms of this mundane looking tree, found only within this particular region, possess remarkable regenerative properties that dramatically extend the life-span of complex carbon based organisms.","en")
locList.addLoc("sr_exotic_spices","Exotic Spices","en")
locList.addLoc("sr_exotic_spices_desc","The micro-climate in this area is perfect for the cultivation of rare indigenous plants that are highly profitable in trading.","en")
locList.addLoc("sr_gigalife","Gigalife","en")
locList.addLoc("sr_gigalife_desc","A galactic marvel of unknown origins and indeterminate antiquity, Gigalife is difficult to quantify with any precision. It can be described as a highly complex ecosystem, yet its activity displays properties usually associated with a single organism; its inhabitants interact in a systematic fashion, similar to organs within a body. Whatever it's true nature, this singular 'entity' is clearly not a natural occurrence, but has been engineered through some highly-advanced civilization... or some other unnatural agency.","en")
locList.addLoc("sr_helium_3","Helium-3","en")
locList.addLoc("sr_helium_3_desc","This isotope of helium is used as a fuel in a wide variety of reactors and other energy production facilities.","en")
locList.addLoc("sr_hivaron","Hivaron Stone","en")
locList.addLoc("sr_hivaron_desc","This unusual mineral has found its way into a surprisingly diverse set of applications. It is most notoriously known for its use as a stimulant in laborers... and in formulating slave-driving drugs.","en")
locList.addLoc("sr_hyper_diamond","Hyperdiamond","en")
locList.addLoc("sr_hyper_diamond_desc","Formed solely during supernovas, Hyperdiamond is one of the hardest known substances in the universe. It is used widely in industrial applications, as well as in stellar hull construction.","en")
locList.addLoc("sr_hyperfuels","Hyperfuels","en")
locList.addLoc("sr_hyperfuels_desc","Hyperfuels is a generic term designating a group of rare, semi-stable radioactive isotopes that make for an excellent reactor fuel source.","en")
locList.addLoc("sr_illum","Illum","en")
locList.addLoc("sr_illum_desc","Illum is a luminous metallic substance that continuously distorts the surrounding space-time in its environment, causing severe sensor echoes and ghost signals.","en")
locList.addLoc("sr_iron_wood","Ironwood","en")
locList.addLoc("sr_iron_wood_desc","This precursor plant was seeded on different worlds sometimes during the deep past. Its wood is very pliable when wet, but once it dries it becomes as hard as industrial grade steel, providing a cheap and useful building material.","en")
locList.addLoc("sr_lithium","Lithium","en")
locList.addLoc("sr_lithium_desc","Lithium has a wide range of industrial applications, but is only seldom found in deposits of significant size.","en")
locList.addLoc("sr_meta_organics","Meta-Organic Compounds","en")
locList.addLoc("sr_meta_organics_desc","Although not organic in and of themselves, these compounds serve as precursors for the creation of potent biofuels, as well as artificial soil.","en")
locList.addLoc("sr_neodymium","Primordial Neodymium","en")
locList.addLoc("sr_neodymium_desc","Only found near collapsed stars, Primordial Neodymium has magnetic properties magnified many times beyond its more typical formations. It yields extremely powerful grav lifts and grav plating when used in their construction.","en")
locList.addLoc("sr_neorite","Neorite Crystals","en")
locList.addLoc("sr_neorite_desc","Neorite Crystals refract EM waves perfectly. Their unique optical properties enable the construction of truly flawless sensors.","en")
locList.addLoc("sr_pitharan_dust","Pitharan","en")
locList.addLoc("sr_pitharan_dust_desc","This odd stone is always found in sizable deposits containing a wealth of useful minerals. It has further been found to significantly improve crop yields when mixed into fertilizers.","en")
locList.addLoc("sr_precious_metals","Precious Metals","en")
locList.addLoc("sr_precious_metals_desc","This area contains large veins of precious rare metals, such as Gold and Platinum.","en")
locList.addLoc("sr_precursor_data_cache","Precursor Data Cache","en")
locList.addLoc("sr_precursor_data_cache_desc","An ancient precursor data core is buried close to the heart of this planet. Although it might take decades or even centuries to decode the entirety of its contents, this find offers a unique window into a lost galactic civilization - and its technological supremacy.","en")
locList.addLoc("sr_precursor_nanites","Precursor Nanites","en")
locList.addLoc("sr_precursor_nanites_desc","A temporally displaced chamber found in this area contains ancient nanites. The origin and intended purpose of these nanites is uncertain - they may have been a weapon or a tool, or perhaps both. Be that as it may, this cache offers an incredible find for anyone who dares unseal it.","en")
locList.addLoc("sr_pyronium","Pyronium Crystals","en")
locList.addLoc("sr_pyronium_desc","Despite its dangerous appearance and very real effectiveness as an explosive, this shimmering, fiery-tinted crystal is highly stable and remains inert in even harsh environments. Only a specific chain of chemical reactions will trigger the release of its destructive energy. Launchers using such warheads may reload and fire considerably faster, as they're safer than typical launchers.","en")
locList.addLoc("sr_sactajenian","Sactajenian","en")
locList.addLoc("sr_sactajenian_desc","Sactajenian is a complex organic compound that can be distilled into a potent liquid drug. This drug creates a strong euphoric feeling, and for some psychically endowed subjects, it can trigger potent psionic powers with long-lasting effects. The unique qualities of this drug fetch a very high price in galactic markets.","en")
locList.addLoc("sr_satramene_gas","Satramene Gas","en")
locList.addLoc("sr_satramene_gas_desc","A highly toxic biological compound, Satramene has some industrial applications in the creation of ultra-strong solvents, but its primary use is in the creation of mind-control drugs.","en")
locList.addLoc("sr_scritch","Scritch","en")
locList.addLoc("sr_scritch_desc","Scritch was considered a theoretically impossible element until it was first discovered. Astonishingly, this substance possesses a tensile strength greater than that of Neutronium. Using extremely advanced gravitic funneling technologies, Scritch can be processed into a meta-liquid state, allowing it to be cast and shaped into usable forms. Once it stabilizes, it becomes exceptionally hard.","en")
locList.addLoc("sr_selenemite","Selenemite Ore","en")
locList.addLoc("sr_selenemite_desc","A highly reliable superconductive material that preserves its qualities in any state. It's essential for energy-conductive elements in weapon systems, charging them significantly faster than typical superconductors.","en")
locList.addLoc("sr_sorium_3","Sorium-3","en")
locList.addLoc("sr_sorium_3_desc","Sorium-3 is a rare radioactive isotope used in high-efficiency force-field generators.","en")
locList.addLoc("sr_terraforming_agents","Terraforming Agents","en")
locList.addLoc("sr_terraforming_agents_desc","Terraforming Agents are an abstract strategic resource: this resource reflects the amount of terraforming resources the empire in question has. This resource is not found in deposits at all, instead the amount of this resource an empire gets every month is calculated using an event. This resource is then used for advanced terraforming. When a player has an income of at least 3 terraforming agent points or more, advanced terraforming of regular uninhabitable planets using the $building_terraformer$ building becomes possible.","en")
locList.addLoc("sr_terraforming_bacteria","Terraforming Bacteria","en")
locList.addLoc("sr_terraforming_bacteria_desc","An unknown species dispersed hardy XNA-based bacteria in this region, back when it was uninhabitable. Over the aeons, these bacteria worked tirelessly to produce this planet's current habitability.","en")
locList.addLoc("sr_thullium","Thullium Ore","en")
locList.addLoc("sr_thullium_desc","A highly versatile superconductive metal formed within the immense pressures of carbon-core planets, Thullium has many industrial applications for any spacefaring civilization.","en")
locList.addLoc("sr_thybidium","Thybidium","en")
locList.addLoc("sr_thybidium_desc","Generators have always had an efficiency limit in the transference of generated energy, with some inevitable loss between the primary reaction and the generator itself. Thybidium presents a solution to this problem in a nearly lossless form of shielding for our reactors, increasing energy production several-fold.","en")
locList.addLoc("sr_ventium","Ventium Ore","en")
locList.addLoc("sr_ventium_desc","A blue-green ore, Ventium's potent energy properties were discovered in a deadly explosion demonstrating its effectiveness at energy release.","en")
locList.addLoc("sr_xerodine","Xerodine","en")
locList.addLoc("sr_xerodine_desc","Xerodine has a unique molecular structure that somehow extends into 4-dimensional space. This property can be used to decrease the disruptions in regular space caused by ships moving in and out of hyperspace.","en")
locList.addLoc("sr_zinorine","Zinorine","en")
locList.addLoc("sr_zinorine_desc","This rare substance has unique ionic properties, making it an integral component in the creation of molecular computers.","en")
locList.addLoc("strategic_resources","Strategic Resources","en")
locList.addLoc("tb_ammonia_pits","£blocker Ammonia Pits","en")
locList.addLoc("tb_ammonia_pits_desc","A geological quirk has riddled this region with pits emitting ammonia, making it terribly toxic to most life.","en")
locList.addLoc("tb_ancient_pillars","£blocker Ancient Pillars","en")
locList.addLoc("tb_ancient_pillars_desc","These pillars stand as testament and memorial to some long extinct civilization.","en")
locList.addLoc("tb_apocalyptic_ruins_1","£blocker Devastated Ruins","en")
locList.addLoc("tb_apocalyptic_ruins_1_desc","Once a sprawling city built by an advanced civilization, this area is now littered with ruins, destroyed by bombardment.","en")
locList.addLoc("tb_apocalyptic_ruins_2","£blocker Bombarded Colony","en")
locList.addLoc("tb_apocalyptic_ruins_2_desc","This former xeno colony was obliterated by spacecraft in some distantly past age.","en")
locList.addLoc("tb_apocalyptic_ruins_3","£blocker War-torn Ruins","en")
locList.addLoc("tb_apocalyptic_ruins_3_desc","Extensive ruins show once great structures reduced to rubble by some unknown, spacefaring enemy.","en")
locList.addLoc("tb_apocalyptic_ruins_4","£blocker Annihilated Ruins","en")
locList.addLoc("tb_apocalyptic_ruins_4_desc","This area is littered with the debris and remnants of some ancient civilization, wiped out by powerful orbital weaponry.","en")
locList.addLoc("tb_apocalyptic_ruins_5","£blocker Shattered City","en")
locList.addLoc("tb_apocalyptic_ruins_5_desc","An apocalyptic blow tore this incredible apart thousands of years ago, leaving only shattered ruins.","en")
locList.addLoc("tb_catastrophe_aftermath_irradiated_desolation","£blocker Desolate Deadlands","en")
locList.addLoc("tb_catastrophe_aftermath_irradiated_desolation_desc","A radiological catastrophe eliminated all life in this area, leaving only oppresively quiet deadlands.","en")
locList.addLoc("tb_catastrophe_aftermath_pyroclastic_terrain_1","£blocker Lava-Bathed Terrain","en")
locList.addLoc("tb_catastrophe_aftermath_pyroclastic_terrain_1_desc","Rent planetary crust has devastated this terrain, bathing it perpetually in volcanic ash and lava.","en")
locList.addLoc("tb_catastrophe_aftermath_pyroclastic_terrain_2","£blocker Pyroclastic Aftermath","en")
locList.addLoc("tb_catastrophe_aftermath_pyroclastic_terrain_2_desc","The aftermath of a terrible event has rendered this region uninhabitably full of pyroclastic activity.","en")
locList.addLoc("tb_catastrophe_erupting_volcano_1","£blocker Erupting Volcano","en")
locList.addLoc("tb_catastrophe_erupting_volcano_1_desc","The geological 'tap' of this land was direly mismanaged. An angry volcano has been left behind in perpetuity.","en")
locList.addLoc("tb_catastrophe_erupting_volcano_2","£blocker Volcanic Aftermath","en")
locList.addLoc("tb_catastrophe_erupting_volcano_2_desc","Tinkering with the planet proved more risk than reward this time, tearing open a powerful volcano that shows no signs of dying anytime soon.","en")
locList.addLoc("tb_catastrophe_mega_geyser","£blocker Towering Geyser","en")
locList.addLoc("tb_catastrophe_mega_geyser_desc","An unfortunate confluence of underground water systems and terran boring created this massive geyser, slaying all typical life in the region surrounding it.","en")
locList.addLoc("tb_catastrophe_tectonic_fracture","£blocker Tectonic Fracture","en")
locList.addLoc("tb_catastrophe_tectonic_fracture_desc","Attempts to redirect an entire planet's geological systems are fraught with danger. This is only one of the calamities that can befall such hubris.","en")
locList.addLoc("tb_chtonian_monstrosity","£blocker Cthonian Monstrosity","en")
locList.addLoc("tb_chtonian_monstrosity_desc","The sole positive note in reports on this terrifying abomination is that it does not venture outside of its domain.","en")
locList.addLoc("tb_cold_desert","£blocker Cold Desert","en")
locList.addLoc("tb_cold_desert_desc","A region where the air is very cold and carries little moisture, so little precipitation occurs. What snow does fall is carried along by strong winds and may form blizzards, drifts and dunes.","en")
locList.addLoc("tb_dark_forest","£blocker Dark Forest","en")
locList.addLoc("tb_dark_forest_desc","This region is overgrown with dark forest. Vegitation on ammonia worlds recieve less light and so has evolved broad dark leaves to aid photosynthesis.","en")
locList.addLoc("tb_deep_marshes","£blocker Deep Marshes","en")
locList.addLoc("tb_deep_marshes_desc","Unlike most swamps, these marshes have incredible depths to them, creating an ecosystem as fascinating as it is hazardous.","en")
locList.addLoc("tb_dense_dark_forest","£blocker Dense Dark Forest","en")
locList.addLoc("tb_dense_dark_forest_desc","This entire region is completely overgrown with thick, impassable dark forest. Vegitation on ammonia worlds recieve less light and so has evolved broad dark leaves to aid photosynthesis.","en")
locList.addLoc("tb_dense_forest","£blocker Dense Forest","en")
locList.addLoc("tb_dense_forest_desc","This entire region is completely overgrown with thick, impassable boreal forest.","en")
locList.addLoc("tb_depleted_terrain","£blocker Depleted Terrain","en")
locList.addLoc("tb_depleted_terrain_desc","Rapacious utilization of all available resources, by necessity, yields utterly depleted terrain.","en")
locList.addLoc("tb_desert","£blocker Desert","en")
locList.addLoc("tb_desert_desc","A barren area of terrain where little precipitation occurs, living conditions are hostile for plant and animal life.","en")
locList.addLoc("tb_devil_weed","£blocker Devil Weed","en")
locList.addLoc("tb_devil_weed_desc","As stubborn as it is noxious, this prolific weed dominates the region, resisting all attempts at domestication.","en")
locList.addLoc("tb_fe_submerged_city","£blocker Submerged City","en")
locList.addLoc("tb_fe_submerged_city_desc","An ancient city constructed by a highly advanced civilization rests beneath these waves, forgotten long, long ago.","en")
locList.addLoc("tb_flesh_freeze_topography","£blocker Flash Freeze Topography","en")
locList.addLoc("tb_flesh_freeze_topography_desc","The topography of this region creates a phenomenon of extreme rapid freezing that overwhelms even advanced shielding, preventing habitation.","en")
locList.addLoc("tb_forest","£blocker Forest","en")
locList.addLoc("tb_forest_desc","This region is overgrown with boreal forest.","en")
locList.addLoc("tb_geomorphological_fractures","£blocker Geomorphological Fractures","en")
locList.addLoc("tb_geomorphological_fractures_desc","Extreme weather conditions in this region has created a maze-like natural structures of rock and ice, inhibiting any systematic use of the area.","en")
locList.addLoc("tb_geyser_fields","£blocker Geyser Fields","en")
locList.addLoc("tb_geyser_fields_desc","Local hydrogeological conditions make this area rich in geysers, but poor for habitation and construction.","en")
locList.addLoc("tb_giant_kelp","£blocker Giant Kelp","en")
locList.addLoc("tb_giant_kelp_desc","A thick forest of giant kelp covers the ocean in this region.","en")
locList.addLoc("tb_grazing_wildlife","£blocker Grazing Wildlife","en")
locList.addLoc("tb_grazing_wildlife_desc","There is abundant wildlife in this region, with many grazing animals and a few predators.","en")
locList.addLoc("tb_icefield","£blocker Icefield","en")
locList.addLoc("tb_icefield_desc","An Icefield has formed in this region. Uninterrupted by glacial channels, falling snow has turned into a thick sheet of ice across the landscape.","en")
locList.addLoc("tb_jungle","£blocker Jungle","en")
locList.addLoc("tb_jungle_desc","This region is overgrown with thick jungle.","en")
locList.addLoc("tb_lava_flows","£blocker Lava Flows","en")
locList.addLoc("tb_lava_flows_desc","Lava persistently flows through breaches in this region's crust.","en")
locList.addLoc("tb_massive_canyons","£blocker Gargantuan Canyons","en")
locList.addLoc("tb_massive_canyons_desc","These massive canyons are utterly hostile to sensible architecture.","en")
locList.addLoc("tb_massive_cavern","£blocker Enormous Cavern","en")
locList.addLoc("tb_massive_cavern_desc","Just beneath the surface of the land here lies an enormous and structurally unsound cavern, making habitation impossible.","en")
locList.addLoc("tb_massive_cliffs","£blocker Towering Cliffs","en")
locList.addLoc("tb_massive_cliffs_desc","Vast and towering, these cliffs make projects of the area incredibly inefficient.","en")
locList.addLoc("tb_natural_paradise_1","£blocker Ineffable Landscape","en")
locList.addLoc("tb_natural_paradise_1_desc","Though its bounty may be quantifiable, the beauty of this land is indescribable.","en")
locList.addLoc("tb_natural_paradise_2","£blocker Pristine Sanctuary","en")
locList.addLoc("tb_natural_paradise_2_desc","This untouched region glimmers with natural beauty.","en")
locList.addLoc("tb_natural_paradise_3","£blocker Living Paradise","en")
locList.addLoc("tb_natural_paradise_3_desc","Everything about this region can be described as a true paradise.","en")
locList.addLoc("tb_pc_asteroidal_carbometallic_rock","Carbometallic Rock","en")
locList.addLoc("tb_pc_asteroidal_carbometallic_rock_desc","This tile is filled with dense carbometallic rock. Mining this tile and clearing the tile blocker £blocker will increase the Asteroidal's output by §G2 Monthly Minerals§! for a 10 year period.","en")
locList.addLoc("tb_pc_asteroidal_energy_rich_rock","Energy Rich Rock","en")
locList.addLoc("tb_pc_asteroidal_energy_rich_rock_desc","This tile is filled with energy rich carbometallic rock. Mining this tile and clearing the tile blocker £blocker will increase the Asteroidal's output by §G4 Monthly Energy§! for a 10 year period.","en")
locList.addLoc("tb_pc_asteroidal_energy_ultra_rich_rock","Energy Ultra-Rich Rock","en")
locList.addLoc("tb_pc_asteroidal_energy_ultra_rich_rock_desc","This tile is filled with ultra-rich energy rock. Mining this tile and clearing the tile blocker £blocker will increase the Asteroidal's output by §G8 Monthly Energy§! for a 10 year period.","en")
locList.addLoc("tb_pc_asteroidal_mineral_rich_rock","Mineral Rich Rock","en")
locList.addLoc("tb_pc_asteroidal_mineral_rich_rock_desc","This tile is filled with mineral rich rock. Mining this tile and clearing the tile blocker £blocker will increase the Asteroidal's output by §G4 Monthly Minerals§! for a 10 year period.","en")
locList.addLoc("tb_pc_asteroidal_mineral_ultra_rich_rock","Mineral Ultra-Rich Rock","en")
locList.addLoc("tb_pc_asteroidal_mineral_ultra_rich_rock_desc","This tile is filled with ultra-rich mineral rock. Mining this tile and clearing the tile blocker £blocker will increase the Asteroidal's output by §G8 Monthly Minerals§! for a 10 year period.","en")
locList.addLoc("tb_pc_barren_terraforming_dust_steppe","£blocker Dust Steppes","en")
locList.addLoc("tb_pc_barren_terraforming_dust_steppe_desc","The soil in these steppes is broken and scattered by periodic winds; everything here is covered in thick layers of loamy dust.","en")
locList.addLoc("tb_pc_barren_terraforming_inactive_volcano","£blocker Dormant Volcano","en")
locList.addLoc("tb_pc_barren_terraforming_inactive_volcano_desc","Geologic shifts have left this volcano dormant, but it still dominates the region and requires significant landscaping to repurpose.","en")
locList.addLoc("tb_pc_barren_terraforming_rugged_land","£blocker Rugged Lands","en")
locList.addLoc("tb_pc_barren_terraforming_rugged_land_desc","Rugged in the extreme, these lands challenge even frontier settlers beyond all reason.","en")
locList.addLoc("tb_pc_barren_terraforming_wind_mountains","£blocker Windy Mountains","en")
locList.addLoc("tb_pc_barren_terraforming_wind_mountains_desc","Winds reach excessive speeds around these mountains, defying any attempt to colonize the area.","en")
locList.addLoc("tb_pc_crystal_asteroidal_crystal_formation","Subspace Crystal Formation","en")
locList.addLoc("tb_pc_crystal_asteroidal_crystal_formation_desc","This tile is filled with exotic matter encased by primordial ice. Mining this tile and clearing the tile blocker £blocker will will increase the Asteroidal's output by §G4 Monthly Physics$ and §G4 Monthly Engineering§! for a 10 year period.","en")
locList.addLoc("tb_pc_crystal_asteroidal_dimensional_phenomena","Dimensional Phenomena","en")
locList.addLoc("tb_pc_crystal_asteroidal_dimensional_phenomena_desc","Extra-dimensional energies permeate this area and warp space and time. Extensive study of these phenomena will greatly expand our knowledge of the structure of the multiverse.","en")
locList.addLoc("tb_pc_frozen_terraforming_deep_valley","£blocker Frozen Ravines","en")
locList.addLoc("tb_pc_frozen_terraforming_deep_valley_desc","These deep ravines are filled with ice; they will need to be cleared out to become usable.","en")
locList.addLoc("tb_pc_frozen_terraforming_frozen_desolation","£blocker Desolate Ice","en")
locList.addLoc("tb_pc_frozen_terraforming_frozen_desolation_desc","Thin ice sheets and frozen winds make this region desolate and barren.","en")
locList.addLoc("tb_pc_greenhouse_cold_terraforming_rugged_land","£blocker Barren Lands","en")
locList.addLoc("tb_pc_greenhouse_cold_terraforming_rugged_land_desc","Freezing winds persistently scour this stretch, preventing habitation.","en")
locList.addLoc("tb_pc_greenhouse_cold_terraforming_sufuric_vents","£blocker Sulfur Vents","en")
locList.addLoc("tb_pc_greenhouse_cold_terraforming_sufuric_vents_desc","This region belches out sulfur plumes at an alarming rate, rendering it uninhabitable.","en")
locList.addLoc("tb_pc_greenhouse_hot_terraforming_rugged_terrain","£blocker Rugged Lands","en")
locList.addLoc("tb_pc_greenhouse_hot_terraforming_rugged_terrain_desc","Rugged in the extreme, these lands challenge even frontier settlers beyond all reason.","en")
locList.addLoc("tb_pc_greenhouse_hot_terraforming_stormfield","£blocker Storm Field","en")
locList.addLoc("tb_pc_greenhouse_hot_terraforming_stormfield_desc","This mostly flat area would have been perfect for construction, if not for the constant electrical discharges at unpredictable intervals.","en")
locList.addLoc("tb_pc_hot_greenhouse_terraforming_stormfield","£blocker Storm Fields","en")
locList.addLoc("tb_pc_hot_greenhouse_terraforming_stormfield_desc","Storms persistently ravage these fields with lightning, preventing life from taking root.","en")
locList.addLoc("tb_pc_hot_greenhouse_terraforming_wastelands","£blocker Wastelands","en")
locList.addLoc("tb_pc_hot_greenhouse_terraforming_wastelands_desc","Poor soil and toxic gases make this terrain unusable.","en")
locList.addLoc("tb_pc_ice_asteroidal_frozen_exotic_matter","Frozen Exotic Matter","en")
locList.addLoc("tb_pc_ice_asteroidal_frozen_exotic_matter_desc","This tile is filled with exotic matter encased by primordial ice. Mining this tile and clearing the tile blocker £blocker will will increase the Asteroidal's output by §G6 Monthly Physics$ and §G4 Monthly Energy§! for a 10 year period.","en")
locList.addLoc("tb_pc_ice_asteroidal_primordial_ice","Primordial Ice","en")
locList.addLoc("tb_pc_ice_asteroidal_primordial_ice_desc","This tile is filled with ice that formed shortly after the universe began. Mining this tile and clearing the tile blocker £blocker will will increase the Asteroidal's output by §G2 Monthly Energy§! and §G0.5 Monthly $sr_terraforming_agents$$! for a 10 year period.","en")
locList.addLoc("tb_pc_molten_terraforming_magma_river","£blocker Magma River","en")
locList.addLoc("tb_pc_molten_terraforming_magma_river_desc","An enormous river of magma continuously flows here.","en")
locList.addLoc("tb_pc_molten_terraforming_pyroclastic_terrain","£blocker Pyroclastic Fields","en")
locList.addLoc("tb_pc_molten_terraforming_pyroclastic_terrain_desc","Raging tectonic turbulence keeps this region peppered with pyroclastic activity.","en")
locList.addLoc("tb_pc_toxic_sulphoric_flora","£blocker Sulphoric Flora","en")
locList.addLoc("tb_pc_toxic_sulphoric_flora_desc","Flora here releases large amounts of sulphur from its metabolic activities, poisoning the air.","en")
locList.addLoc("tb_pc_toxic_terraforming_noxious_concentrations","£blocker Noxious Concentration","en")
locList.addLoc("tb_pc_toxic_terraforming_noxious_concentrations_desc","Heavy elements have mixed into the gases in this area, rendering it utterly uninhabitable.","en")
locList.addLoc("tb_persistent_volcano","£blocker Persistent Volcano","en")
locList.addLoc("tb_persistent_volcano_desc","There is an active volcano in this region, that is being fed from a plume above a mantle hotspot.\n\n§RDeep Crust Engineering will have no effect, only terraforming can remove this volcano.§!","en")
locList.addLoc("tb_planetary_projects_deep_core_mining_engine","£blocker Deep Core Mining Engine","en")
locList.addLoc("tb_planetary_projects_deep_core_mining_engine_desc","This massive, gravitic-stabilized tower utilizes orbital arrays to power immense drilling operations on a scale previously unseen - but strip-mining of this magnitude may have catastrophic long-term side effects.","en")
locList.addLoc("tb_planetary_projects_geothermal_tap_engine","£blocker Geothermal Power Uplink","en")
locList.addLoc("tb_planetary_projects_geothermal_tap_engine_desc","Widescale fracking efforts of the region culminate in a specialized refinery here, where the immense power generated can be beamed wherever needed - but the region cannot sustain such invasive fracturing indefinitely.","en")
locList.addLoc("tb_planetary_projects_harvest_engine","£blocker Soil Harvester","en")
locList.addLoc("tb_planetary_projects_harvest_engine_desc","These gigantic machines harvest organic substances and liquids. They produce large amounts of $food$ as well as some degree of $sr_terraforming_agents$ but deplete the planet's soil in the process.","en")
locList.addLoc("tb_precursor_ruins_1","£blocker Precursor Ruins","en")
locList.addLoc("tb_precursor_ruins_1_desc","Ancient towers dominate the skyline for miles in this region, still standing despite being built millions of years ago.","en")
locList.addLoc("tb_precursor_ruins_10","£blocker Precursor Ruins","en")
locList.addLoc("tb_precursor_ruins_10_desc","Natural processes have failed to crumble rows of massive towers left behind by an ancient civilization.","en")
locList.addLoc("tb_precursor_ruins_2","£blocker Precursor Ruins","en")
locList.addLoc("tb_precursor_ruins_2_desc","Rugged canyons and jagged mountains here are riddled with structures still largely functional and habitable - even though they predate our surveys by millennia.","en")
locList.addLoc("tb_precursor_ruins_3","£blocker Precursor Ruins","en")
locList.addLoc("tb_precursor_ruins_3_desc","Glimmering structures are glimpsable amidst the shifting sands of this desert, waiting to be excavated and restored to use.","en")
locList.addLoc("tb_precursor_ruins_4","£blocker Precursor Ruins","en")
locList.addLoc("tb_precursor_ruins_4_desc","Ruins of an incredible city fill this region, still habitable and operational - but mysteriously empty of any sapient life forms.","en")
locList.addLoc("tb_precursor_ruins_5","£blocker Precursor Ruins","en")
locList.addLoc("tb_precursor_ruins_5_desc","A multi-layered city casts vast shadows across the land, culminating in an enormous disc atop an ancient pillar; it stands in defiance of the millions of years of neglect and abandonment.","en")
locList.addLoc("tb_precursor_ruins_6","£blocker Precursor Ruins","en")
locList.addLoc("tb_precursor_ruins_6_desc","The river-cliffs of this region have been built over by some lost civilization, their artwork and technology standing the test of time without them.","en")
locList.addLoc("tb_precursor_ruins_7","£blocker Precursor Ruins","en")
locList.addLoc("tb_precursor_ruins_7_desc","Gigantic structures overlook largely barren valleys, the sole remnants of some ancient Precursor left behind on this planet.","en")
