locList.addLoc("pc_greenhouse_cold_terraforming_tile_desc","Terreno hostil muito frio, com dunas, rios, lagos e mares de estranhos líquidos.","pt")
locList.addLoc("pc_greenhouse_hot","Mundo Redutor Quente","pt")
locList.addLoc("pc_greenhouse_hot_desc","Um planeta rochoso com uma atmosfera espessa e de alta pressão. Os gases na atmosfera causaram um efeito estufa descontrolado, criando temperaturas superficiais muito altas.","pt")
