locList.addLoc("sm_sb_building_illum_spectral_emitters_desc","Emite um campo constante de ecos espectrais e distorções espaciais, confundindo sensores de navios inimigos no sistema.","pt")
locList.addLoc("sm_sb_building_living_research_facility","Instalação de pesquisa de metal vivo","pt")
