locList.addLoc("sm_sb_building_lythuric_reactor","Reator Lythuric","pt")
locList.addLoc("sm_sb_building_lythuric_reactor_desc","Aproveita as potentes propriedades energéticas do gás líthurico para fornecer potência e uma ferramenta potente para a experimentação física.","pt")
locList.addLoc("sm_sb_building_neodymium_integrity_field_generator","Gerador de campo de integridade de neodímio","pt")
locList.addLoc("sm_sb_building_neodymium_integrity_field_generator_desc","Reforça os navios amistosos no sistema com um campo de integridade de balaustrada de neodímio, melhorando drasticamente a resistência a danos.","pt")
locList.addLoc("sm_sb_building_neorite_targeting_array","Matriz de segmentação de Neorite","pt")
locList.addLoc("sm_sb_building_neorite_targeting_array_desc","Utiliza sensores especiais Neorite para criar uma bateria de matriz, melhorando a eficácia da segmentação de todos os navios amigáveis ​​do sistema.","pt")
locList.addLoc("sm_sb_building_neutronium_forge","Forja de Neutrónio","pt")
